import React, { useState, useEffect } from "react";
import axios from "axios"; // Importer Axios
import "../styles/AdminGenerateCR.css"; // Fichier CSS spécifique
import reportIcon from "../assets/generate.png";
import logoutIcon from "../assets/logout.png";
import profilePic from "../assets/user.png";
import logoIcon from "../assets/logo.png";
import crIcon from "../assets/cr.png";

const AdminGenerateCR = () => {
    const [reports, setReports] = useState([]); // Pour stocker les rapports générés
    const [newReport, setNewReport] = useState({
        title: "",
        description: "",
        date: ""
    });

    // Fonction pour récupérer les rapports générés avec le token
    useEffect(() => {
        const token = localStorage.getItem("token"); // Récupérer le token depuis le localStorage
        if (token) {
            axios.get("https://ton-api.com/reports", {
                headers: {
                    "Authorization": `Bearer ${token}` // Ajouter le token dans l'en-tête
                }
            })
                .then(response => {
                    setReports(response.data); // Mettre à jour l'état avec la liste des rapports
                })
                .catch(error => {
                    console.error("Erreur lors de la récupération des rapports : ", error);
                });
        } else {
            console.error("Token non trouvé. Veuillez vous reconnecter.");
        }
    }, []);

    // Fonction pour gérer l'ajout d'un nouveau rapport
    const handleAddReport = (event) => {
        event.preventDefault();
        const token = localStorage.getItem("token"); // Récupérer le token depuis le localStorage
        if (token) {
            axios.post("https://ton-api.com/reports", newReport, {
                headers: {
                    "Authorization": `Bearer ${token}` // Ajouter le token dans l'en-tête
                }
            })
                .then(response => {
                    setReports([...reports, response.data]); // Ajouter le rapport à la liste
                    setNewReport({ title: "", description: "", date: "" }); // Réinitialiser le formulaire
                })
                .catch(error => {
                    console.error("Erreur lors de l'ajout du rapport : ", error);
                });
        } else {
            console.error("Token non trouvé. Veuillez vous reconnecter.");
        }
    };

    // Gérer les changements dans le formulaire
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewReport({
            ...newReport,
            [name]: value
        });
    };

    return (
        <div className="admin-container">
            {/* Sidebar */}
            <aside className="sidebar">
                <div className="logo-container">
                    <img src={logoIcon} alt="logo" className="logo-icon" />
                    <span className="logo-text">MYCERTS</span>
                </div>
                <nav>
                    <ul>
                        <li>
                            <img src={reportIcon} alt="Generate CR" />
                            <span>Generate CR</span>
                        </li>
                    </ul>
                </nav>
                <button className="logout-btn">
                    <img src={logoutIcon} alt="Log Out" />
                    <span>Log Out</span>
                </button>
            </aside>

            {/* Main Content */}
            <main className="content">
                {/* Header */}
                <div className="header">
                    <h2>
                        <img src={crIcon} alt="CR" className="header-icon" />
                        <span>Generate CR</span>
                    </h2>
                    <div className="profile">
                        <span>S. Admin</span>
                        <img src={profilePic} alt="Admin Profile" />
                    </div>
                </div>

                {/* Reports Form */}
                <div className="form-container">
                    <button className="add-btn" onClick={() => {}}>Add Report</button>
                    <form className="report-form" onSubmit={handleAddReport}>
                        <label>Title :</label>
                        <input
                            type="text"
                            name="title"
                            value={newReport.title}
                            onChange={handleInputChange}
                        />

                        <label>Description :</label>
                        <textarea
                            name="description"
                            value={newReport.description}
                            onChange={handleInputChange}
                        />

                        <label>Date :</label>
                        <input
                            type="date"
                            name="date"
                            value={newReport.date}
                            onChange={handleInputChange}
                        />

                        <div className="form-buttons">
                            <button className="cancel-btn">Cancel</button>
                            <button className="add-btn" type="submit">Add</button>
                        </div>
                    </form>
                </div>
            </main>

            {/* Reports List */}
            <aside className="reports-list">
                <h3>📄 Reports</h3>
                <ul>
                    {reports.map((report, index) => (
                        <li key={index}>{report.title} - {report.date}</li>
                    ))}
                </ul>
            </aside>
        </div>
    );
};

export default AdminGenerateCR;
