import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/AdminEvent.css";
import eventIcon from "../assets/events.png";
import organizerIcon from "../assets/organizers.png";
import reportIcon from "../assets/generate.png";
import logoutIcon from "../assets/logout.png";
import profilePic from "../assets/user.png";
import eventImage from "../assets/event.png";
import logoIcon from "../assets/logo.png";

const AdminEvent = () => {
    const [events, setEvents] = useState([]); // Pour stocker les événements
    const [loading, setLoading] = useState(true); // Pour gérer le chargement des événements
    const [error, setError] = useState(""); // Pour afficher les erreurs si l'API échoue

    // Fonction pour récupérer les événements depuis l'API
    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:3001/events'); // Remplace par l'URL réelle de ton API
                setEvents(response.data); // Remplir le state avec les événements récupérés
                setLoading(false); // Fin du chargement
            } catch (error) {
                setError("Erreur lors du chargement des événements");
                setLoading(false);
            }
        };

        fetchEvents(); // Appel de la fonction pour récupérer les événements
    }, []); // Le tableau vide [] signifie que l'effet se déclenche une seule fois après le premier rendu

    return (
        <div className="admin-container">
            {/* Sidebar */}
            <aside className="sidebar">
                <div className="logo-container">
                    <img src={logoIcon} alt="logo" className="logo-icon" />
                    <span className="logo-text">MYCERTS</span>
                </div>
                <nav>
                    <ul>
                        <li className="active">
                            <img src={eventIcon} alt="Events" />
                            <span>Events</span>
                        </li>
                        <li>
                            <img src={organizerIcon} alt="Organizers" />
                            <span>Organizers</span>
                        </li>
                        <li>
                            <img src={reportIcon} alt="Generate CR" />
                            <span>Generate CR</span>
                        </li>
                    </ul>
                </nav>
                <button className="logout-btn">
                    <img src={logoutIcon} alt="Log Out" />
                    <span>Log Out</span>
                </button>
            </aside>

            {/* Main Content */}
            <main className="content">
                {/* Header */}
                <div className="header">
                    <h2>📅 <span>Events</span></h2>
                    <div className="profile">
                        <span>S. Admin</span>
                        <img src={profilePic} alt="Admin Profile" />
                    </div>
                </div>

                {/* Events List */}
                <div className="events-container">
                    {loading ? (
                        <p>Loading events...</p> // Affiche un message de chargement
                    ) : error ? (
                        <p>{error}</p> // Affiche un message d'erreur en cas de problème
                    ) : (
                        events.map((event, index) => (
                            <div className="event-card" key={index}>
                                <img src={eventImage} alt={event.title} className="event-img" />
                                <div className="event-info">
                                    <h4>{event.title}</h4>
                                    <p className="event-date">{event.date}</p>
                                    <p className="event-description">{event.description}</p>
                                </div>
                                <button className="edit-btn">Edit</button>
                            </div>
                        ))
                    )}
                    <button className="add-event-btn">Add Event</button>
                </div>
            </main>

            {/* Calendar */}
            <div className="calendar">
                <h2>CALENDAR 2025</h2>
                <div className="months">
                    {["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
                        .map((month, index) => (
                            <span key={index} className={month === "April" || month === "March" ? "highlight" : ""}>
                                {month.toLowerCase()}
                            </span>
                        ))}
                </div>
            </div>
        </div>
    );
};

export default AdminEvent;
