import React from 'react';
import '../styles/AdminEvent.css';

// Import icons/images
import eventImg from '../assets/event.png';
import userImg from '../assets/user.png';
import calendarImg from '../assets/calendar.png';
import generateImg from '../assets/generate.png';
import logoutImg from '../assets/logout.png';
import organizersImg from '../assets/organizers.png';
import profileImg from '../assets/profile.png';

const AdminEvent = () => {
    return (
        <div className="admin-event-container">
            {/* Sidebar */}
            <nav className="sidebar">
                <h2 className="logo">ðŸŽ– MYCERTS</h2>
                <ul>
                    <li><img src={eventImg} alt="Events" /> Events</li>
                    <li><img src={organizersImg} alt="Organizers" /> Organizers</li>
                    <li><img src={generateImg} alt="Generate" /> Generate CR</li>
                </ul>
                <div className="logout">
                    <img src={logoutImg} alt="Logout" />
                    <span>Log Out</span>
                </div>
            </nav>

            {/* Main Content */}
            <main className="admin-event-content">
                <div className="header">
                    <h1>ðŸ“… Events</h1>
                    <div className="profile">
                        <span>S.Admin<br /><small>Prof</small></span>
                        <img src={profileImg} alt="Profile" />
                    </div>
                </div>

                {/* Events List */}
                <div className="events-list">
                    <div className="event-card">
                        <div className="event-details">
                            <h3>Marketing Digital</h3>
                            <p>March 2025</p>
                            <p>Discover essential digital marketing strategies...</p>
                        </div>
                        <button className="edit-btn">Edit</button>
                    </div>

                    <div className="event-card">
                        <div className="event-details">
                            <h3>Machine Learning</h3>
                            <p>April 2025</p>
                            <p>Get started with AI and machine learning...</p>
                        </div>
                        <button className="edit-btn">Edit</button>
                    </div>

                    <button className="add-event">Add Event</button>
                </div>
            </main>
        </div>
    );
};

export default AdminEvent;
