import React from 'react';
import '../styles/AdminEvent.css'; // Corrected path for CSS


// Corrected import paths for assets (images)
import eventImg from '../assets/event.png'; 
import userImg from '../assets/user.png';
import calendarImg from '../assets/calendar.png';
import generateImg from '../assets/generate.png';
import logoutImg from '../assets/logout.png';
import organizersImg from '../assets/organizers.png';

const AdminEvent = () => {
    return (
        <div className="admin-event-container">
            <nav className="sidebar">
                <ul>
                    <li><img src={eventImg} alt="Events" /> Events</li>
                    <li><img src={userImg} alt="Users" /> Users</li>
                    <li><img src={calendarImg} alt="Calendar" /> Calendar</li>
                    <li><img src={generateImg} alt="Generate" /> Generate Certificates</li>
                    <li><img src={organizersImg} alt="Organizers" /> Organizers</li>
                    <li><img src={logoutImg} alt="Logout" /> Logout</li>
                </ul>
            </nav>
            <main className="admin-event-content">
                <h1>Admin Event Management</h1>
                <p>Manage and oversee all events from this panel.</p>
            </main>
        </div>
    );
};

export default AdminEvent;
