import React, { useState, useEffect } from "react";
import axios from "axios"; // Importer Axios
import "../styles/AdminEvent.css";
import eventIcon from "../assets/events.png";
import certificateIcon from "../assets/certificate.png";
import logoutIcon from "../assets/logout.png";
import profilePic from "../assets/user.png";
import logoIcon from "../assets/logo.png";
import { FaCertificate, FaUpload } from "react-icons/fa";

const PARcertificates = () => {
  const [certificates, setCertificates] = useState([]); // État pour gérer les certificats
  const [loading, setLoading] = useState(true); // État pour gérer le chargement des données

  // Utiliser useEffect pour récupérer les certificats dès que le composant est monté
  useEffect(() => {
    const fetchCertificates = async () => {
      try {
        const response = await axios.get("https://ton-api.com/certificates"); // Remplace par ton URL d'API
        setCertificates(response.data); // Mettre à jour l'état avec les certificats récupérés
      } catch (error) {
        console.error("Erreur lors de la récupération des certificats:", error);
      } finally {
        setLoading(false); // Lorsque les données sont récupérées ou une erreur est survenue, on désactive le chargement
      }
    };

    fetchCertificates(); // Appeler la fonction pour récupérer les certificats
  }, []); // Le tableau vide [] signifie que l'effet ne se lance qu'une seule fois après le premier rendu du composant

  return (
    <div className="admin-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="logo-container">
          <img src={logoIcon} alt="logo" className="logo-icon" />
          <span className="logo-text">MYCERTS</span>
        </div>
        <nav>
          <ul>
            <li className="active">
              <img src={eventIcon} alt="Events" />
              <span>Events</span>
            </li>
            <li>
              <img src={certificateIcon} alt="Certificates" />
              <span>MY Certificates</span>
            </li>
          </ul>
        </nav>
        <button className="logout-btn">
          <img src={logoutIcon} alt="Log Out" />
          <span>Log Out</span>
        </button>
      </aside>

      {/* Main Content */}
      <main className="content">
        {/* Header */}
        <div className="header">
          <h2>📅 <span>Events</span></h2>
          <div className="profile">
            <span>S. Ahmed</span>
            <img src={profilePic} alt="Admin Profile" />
          </div>
        </div>

        {/* Certificates List */}
        <section className="certificates-list">
          {loading ? (
            <p>Loading certificates...</p> // Afficher un message de chargement pendant la récupération des données
          ) : certificates.length === 0 ? (
            <p>No certificates found.</p> // Afficher un message si aucun certificat n'est trouvé
          ) : (
            certificates.map((cert, index) => (
              <div key={index} className="certificate-item">
                <FaCertificate className="cert-icon" />
                <span>{cert.name}</span> {/* Remplacer par le nom de certificat approprié */}
                <button className="upload-btn">
                  <FaUpload /> Upload
                </button>
              </div>
            ))
          )}
        </section>
      </main>
    </div>
  );
};

export default PARcertificates;
