import React, { useState, useEffect } from "react";
import axios from "axios"; // Importer Axios
import "../styles/AdminEvent.css"; // Assurez-vous que ce fichier est correctement lié
import eventIcon from "../assets/events.png"; // Icône pour la section événements
import certificateIcon from "../assets/certificate.png"; // Icône pour la section certificats
import logoutIcon from "../assets/logout.png"; // Icône de déconnexion
import profilePic from "../assets/user.png"; // Image de profil
import eventImage from "../assets/event.png"; // Illustration dans la carte de l'événement
import logoIcon from "../assets/logo.png"; // Illustration du logo

const PARdashboard = () => {
  const [events, setEvents] = useState([]); // État pour gérer la liste des événements
  const [loading, setLoading] = useState(true); // État pour gérer le chargement des événements

  // Utiliser useEffect pour récupérer les événements depuis l'API
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get("https://ton-api.com/events"); // Remplacer par l'URL de ton API
        setEvents(response.data); // Mettre à jour l'état avec les événements récupérés
      } catch (error) {
        console.error("Erreur lors de la récupération des événements:", error);
      } finally {
        setLoading(false); // Lorsque les données sont récupérées ou une erreur est survenue, on désactive le chargement
      }
    };

    fetchEvents(); // Appeler la fonction pour récupérer les événements
  }, []); // L'effet se lance une seule fois lors du premier rendu du composant

  return (
    <div className="admin-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="logo-container">
          <img src={logoIcon} alt="logo" className="logo-icon" />
          <span className="logo-text">MYCERTS</span>
        </div>
        <nav>
          <ul>
            <li className="active">
              <img src={eventIcon} alt="Events" />
              <span>Events</span>
            </li>
            <li>
              <img src={certificateIcon} alt="Certificates" />
              <span>MY Certificates</span>
            </li>
          </ul>
        </nav>
        <button className="logout-btn">
          <img src={logoutIcon} alt="Log Out" />
          <span>Log Out</span>
        </button>
      </aside>

      {/* Main Content */}
      <main className="content">
        {/* Header */}
        <div className="header">
          <h2>📅 <span>Events</span></h2>
          <div className="profile">
            <span>S. Ahmed</span>
            <img src={profilePic} alt="Admin Profile" />
          </div>
        </div>

        {/* Events List */}
        <div className="events-container">
          {loading ? (
            <p>Loading events...</p> // Afficher un message de chargement pendant la récupération des événements
          ) : events.length === 0 ? (
            <p>No events found.</p> // Afficher un message si aucun événement n'est trouvé
          ) : (
            events.map((event, index) => (
              <div key={index} className="event-card">
                <img src={event.image || eventImage} alt={event.name} className="event-img" />
                <div className="event-info">
                  <h4>{event.name}</h4>
                  <p className="event-date">{event.date}</p>
                  <p className="event-description">{event.description}</p>
                </div>
              </div>
            ))
          )}
        </div>
      </main>

      {/* Calendar */}
      <div className="calendar">
        <h2>CALENDAR 2025</h2>
        <div className="months">
          {["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"].map(
            (month, index) => (
              <span
                key={index}
                className={month === "April" || month === "March" ? "highlight" : ""}
              >
                {month.toLowerCase()}
              </span>
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default PARdashboard;
